import React, { useState } from 'react';
import './App.css';
import { Line } from 'react-chartjs-2';  // Import Line chart component
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

// Register required Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

function App() {
  const [energyData, setEnergyData] = useState([]);
  const [forecast, setForecast] = useState(null);

  const handleFetchData = async () => {
    // Fetch energy consumption data or use dummy data
    const data = [
      { time: '2024-01-01', usage: 50 },
      { time: '2024-01-02', usage: 45 },
      { time: '2024-01-03', usage: 55 },
    ];
    setEnergyData(data);
  };

  const handleForecastData = async () => {
    // A simple forecast logic or use a model
    const forecastedValue = energyData.length
      ? energyData[energyData.length - 1].usage * 1.1
      : 0;
    setForecast(forecastedValue);
  };

  // Prepare chart data
  const chartData = {
    labels: energyData.map((entry) => entry.time), // Time labels
    datasets: [
      {
        label: 'Energy Usage (kWh)',
        data: energyData.map((entry) => entry.usage), // Energy usage values
        borderColor: 'rgba(75, 192, 192, 1)',
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
        fill: true,
      },
    ],
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Energy Consumption Forecasting Tool</h1>
        <p>Predict and manage your energy usage.</p>
        <button onClick={handleFetchData}>Load Energy Data</button>
        <button onClick={handleForecastData}>Forecast Energy Consumption</button>
      </header>

      <div className="dashboard">
        <h2>Energy Usage Data</h2>
        <ul>
          {energyData.map((entry, index) => (
            <li key={index}>
              {entry.time}: {entry.usage} kWh
            </li>
          ))}
        </ul>
      </div>

      <div className="chart">
        <h2>Energy Usage Chart</h2>
        <Line data={chartData} />
      </div>

      <div className="forecast">
        <h2>Forecasted Energy Consumption</h2>
        {forecast !== null ? (
          <p>{forecast} kWh</p>
        ) : (
          <p>Click 'Forecast Energy Consumption' to predict.</p>
        )}
      </div>
    </div>
  );
}

export default App;
