import React, { useState } from 'react';
import './App.css';

function App() {
  const [energyData, setEnergyData] = useState([]);
  const [forecast, setForecast] = useState(null);

  const handleFetchData = async () => {
    // Fetch energy consumption data or use dummy data
    const data = [
      { time: '2024-01-01', usage: 50 },
      { time: '2024-01-02', usage: 45 },
      { time: '2024-01-03', usage: 55 },
    ];
    setEnergyData(data);
  };

  const handleForecastData = () => {
    // A simple forecast logic or use a model
    const forecastedValue = energyData.length
      ? energyData[energyData.length - 1].usage * 1.1
      : 0;
    setForecast(forecastedValue);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Energy Consumption Forecasting Tool</h1>
        <p>Predict and manage your energy usage.</p>
        <button onClick={handleFetchData}>Load Energy Data</button>
        <button onClick={handleForecastData}>Forecast Energy Consumption</button>
      </header>

      <div className="dashboard">
        <h2>Energy Usage Data</h2>
        <ul>
          {energyData.map((entry, index) => (
            <li key={index}>
              {entry.time}: {entry.usage} kWh
            </li>
          ))}
        </ul>
      </div>

      <div className="forecast">
        <h2>Forecasted Energy Consumption</h2>
        {forecast !== null ? (
          <p>{forecast} kWh</p>
        ) : (
          <p>Click 'Forecast Energy Consumption' to predict.</p>
        )}
      </div>
    </div>
  );
}

export default App;
