import React, { useState } from 'react';
import './App.css';
import { Bar, Pie } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

// Register necessary components for Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

function App() {
  const [energyData, setEnergyData] = useState([
    { time: '2024-01-01', usage: 50 },
    { time: '2024-01-02', usage: 45 },
    { time: '2024-01-03', usage: 55 },
    { time: '2024-01-04', usage: 60 },
    { time: '2024-01-05', usage: 52 },
  ]);

  const barChartData = {
    labels: energyData.map((entry) => entry.time),
    datasets: [
      {
        label: 'Energy Usage (kWh)',
        data: energyData.map((entry) => entry.usage),
        backgroundColor: '#4BC0C0', // Color for the bars
        borderColor: '#4BC0C0',
        borderWidth: 1,
      },
    ],
  };

  const pieChartData = {
    labels: ['Usage 1', 'Usage 2', 'Usage 3'],
    datasets: [
      {
        data: [55, 25, 20],
        backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56'],
        hoverOffset: 4,
      },
    ],
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Energy Consumption Forecasting Tool</h1>
        <p className="tagline">Predict and manage your energy usage efficiently.</p>
      </header>

      <section className="content">
        <h2 className="section-heading">Energy Usage Bar Chart</h2>
        <Bar data={barChartData} />

        <h2 className="section-heading">Energy Usage Distribution (Pie Chart)</h2>
        <Pie data={pieChartData} />
      </section>
    </div>
  );
}

export default App;
